import React, { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getProductsList } from "../actions/productActions";
import Message from "../components/Message";
import { Spinner, Row, Col } from "react-bootstrap";
import Product from "../components/Product";
import { useHistory } from "react-router-dom";
import { CREATE_PRODUCT_RESET } from "../constants";
import { Form, Button } from "react-bootstrap";
import { createProduct } from "../actions/productActions";
import { checkTokenValidation, logout } from "../actions/userActions";
import stockImg from "./img-not-found.jpg";


import './home-page.css'; // import CSS styles

function HomePage() {
    const [cards, setCards] = useState([]);

    const [page, setPage] = useState(1);
  
    let history = useHistory();
    let searchTerm = history.location.search;
    const dispatch = useDispatch();
  
    // products list reducer
    const productsListReducer = useSelector((state) => state.productsListReducer);
    const { loading, error, products } = productsListReducer;
  
    // login reducer
    const userLoginReducer = useSelector((state) => state.userLoginReducer);
    const { userInfo } = userLoginReducer;
  
    useEffect(() => {
      if (!userInfo) {
        history.push("/login");
      }
      dispatch(checkTokenValidation());
    }, [dispatch, userInfo, history]);
  
    useEffect(() => {
      dispatch(getProductsList());
      dispatch({
        type: CREATE_PRODUCT_RESET,
      });
      //dispatch(checkTokenValidation())
    }, [dispatch]);
  
    const showNothingMessage = () => {
      return (
        <div>
          {!loading ? <Message variant="info">Nothing to show</Message> : ""}
        </div>
      );
    };    
    // function to handle infinite scrolling
    const handleScroll = () => {
      if (
        window.innerHeight + document.documentElement.scrollTop ===
        document.documentElement.offsetHeight
      ) {
        setPage(page + 1);
      }
    };
  
    useEffect(() => {
      window.addEventListener("scroll", handleScroll);
      return () => window.removeEventListener("scroll", handleScroll);
    }, []);
  
    
    return (
        <div>
            {products.filter((item) => item.product_from ).length === 0
  ? showNothingMessage()
  : (
    <div className="card-container">
      {products
        .filter((item) => item.product_from )
        .map((product, idx) => (
          <div key={product.id} className="card">
            <img src={product.image} alt={product.name} className="profile-image" />
            <div className="card-content">
                
              <h3>{product.name}</h3>
              <p>{product.description}</p>
              <img src={product.image} alt={product.name} className="card-image" />
              <div className="card-actions">
                <div className="icon like">
                  <a href="#">
                    <i className="fas fa-thumbs-up"></i>
                    <span>Like</span>
                  </a>
                </div>
                <div className="icon comment">
                  <a href="#">
                    <i className="fas fa-comment"></i>
                    <span>Comment</span>
                  </a>
                </div>
                <div className="icon share">
                  <a href="#">
                    <i className="fas fa-share"></i>
                    <span>Share</span>
                  </a>
                </div>
                <div className="icon shop">
                  <a href="#">
                    <i className="fas fa-shopping-cart"></i>
                    <span>Shop</span>
                  </a>
                </div>
              </div>
            </div>
          </div>
        ))}
    </div>
  )
}

        </div>
    );
}

export default HomePage;
