import React, { useState, useEffect } from 'react'
import './home-page.css'; // import CSS styles

function HomePage() {
    const [cards, setCards] = useState([]);
    const [loading, setLoading] = useState(true);
    const [page, setPage] = useState(1);
  
    // function to fetch data from API
    const fetchData = async () => {
      setLoading(true);
      const response = await fetch(`https://jsonplaceholder.typicode.com/users?page=${page}&limit=10`);
      const data = await response.json();
      setCards([...cards, ...data]);
      setLoading(false);
    };
  
    useEffect(() => {
      fetchData();
      // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [page]);
  
    // function to handle infinite scrolling
    const handleScroll = () => {
      if (
        window.innerHeight + document.documentElement.scrollTop ===
        document.documentElement.offsetHeight
      ) {
        setPage(page + 1);
      }
    };
  
    useEffect(() => {
      window.addEventListener("scroll", handleScroll);
      return () => window.removeEventListener("scroll", handleScroll);
    }, []);
  
    return (
      <div className="card-container">
        {cards.map((card) => (
          <div key={card.id} className="card">
            <div className="card-header">
              <img src={`https://i.pravatar.cc/150?img=${card.id}`} alt={card.name} />
              <div className="card-header-info">
                <h3>{card.name}</h3>
                <p>{card.email}</p>
              </div>
            </div>
            <div className="card-body">
              <img src={`https://picsum.photos/400/300?random=${card.id}`} alt="" />
              <div className="card-body-info">
                <p>{card.company.catchPhrase}</p>
                <div className="card-icons">
                  <div className="icon">
                    <i className="far fa-heart"></i>
                    <span>12</span>
                  </div>
                  <div className="icon">
                    <i className="far fa-comment"></i>
                    <span>4</span>
                  </div>
                  <div className="icon">
                    <i className="fas fa-share"></i>
                  </div>
                  <div className="icon">
                    <i className="fas fa-shopping-cart"></i>
                  </div>
                </div>
              </div>
            </div>
          </div>
        ))}
        {loading && <div className="loading">Loading...</div>}
      </div>
    );
}

export default HomePage;
