import React, { useEffect } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { getProductsList } from '../actions/productActions'
import Message from '../components/Message'
import { Spinner, Row, Col } from 'react-bootstrap'
import Product from '../components/Product'
import { useHistory } from "react-router-dom";
import { CREATE_PRODUCT_RESET } from '../constants'
import './Product_detail.css';

function ProductsListPage() {

    let history = useHistory()
    let searchTerm = history.location.search
    const dispatch = useDispatch()

    // products list reducer
    const productsListReducer = useSelector(state => state.productsListReducer)
    const { loading, error, products } = productsListReducer

    useEffect(() => {
        dispatch(getProductsList())
        dispatch({
            type: CREATE_PRODUCT_RESET
        })
        //dispatch(checkTokenValidation())
    }, [dispatch])

    const showNothingMessage = () => {
        return (
            <div>
                {!loading ? <Message variant='info'>Nothing to show</Message> : ""}                
            </div>
        )
    }

    return (
        <>
      <header>
        <div class="header-left">Fashio.</div>
        <div class="header-right">
          <i class="custom-icon icon1"></i>
          <i class="custom-icon icon2"></i>
          <i class="custom-icon icon3"></i>
          <i class="custom-icon icon4"></i>
          <i class="custom-icon icon5"></i>
        </div>
      </header>
      <h2>NEW POST</h2>
      <main>

        <form>
          <textarea placeholder="Enter product description"></textarea>
        </form>
        <div class="buttons">
          <button class="star-button">Add Star</button>
          <button class="image-button">Add Image</button>
          <button class="post-button">Post</button>
        </div>
      </main>

    </>
    )
}

export default ProductsListPage
