import React, { useState, useEffect } from 'react';
import './home-page.css'; // import CSS styles

function homePage() {
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(false);
  const [page, setPage] = useState(1);

  useEffect(() => {
    setLoading(true);
    fetch(`https://jsonplaceholder.typicode.com/posts?_page=${page}&_limit=10`)
      .then(response => response.json())
      .then(json => {
        setData(prevData => [...prevData, ...json]);
        setLoading(false);
      })
      .catch(error => {
        console.log(error);
        setLoading(false);
      });
  }, [page]);

  const handleScroll = event => {
    const { scrollTop, clientHeight, scrollHeight } = event.currentTarget;
    if (scrollHeight - scrollTop === clientHeight) {
      setPage(prevPage => prevPage + 1);
    }
  };

  return (
    <div className="container" onScroll={handleScroll}>
      {data.map(item => (
        <div key={item.id} className="card">
          <img src={`https://i.pravatar.cc/150?img=${item.userId}`} alt="Profile" />
          <div className="card-content">
            <h2>{item.title}</h2>
            <p>{item.body}</p>
            <div className="card-actions">
              <button><i className="fa fa-thumbs-up"></i> Like</button>
              <button><i className="fa fa-comment"></i> Comment</button>
              <button><i className="fa fa-share"></i> Share</button>
              <button><i className="fa fa-shopping-cart"></i> Shop</button>
            </div>
          </div>
        </div>
      ))}
      {loading && <div className="loader">Loading...</div>}
    </div>
  );
}

export default homePage;
