import './home-page.css'; // import CSS styles
import React, { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getProductsList } from "../actions/productActions";
import Message from "../components/Message";
import { Spinner, Row, Col } from "react-bootstrap";
import Product from "../components/Product";
import { useHistory } from "react-router-dom";
import { CREATE_PRODUCT_RESET } from "../constants";
import { Form, Button } from "react-bootstrap";
import { createProduct } from "../actions/productActions";
import { checkTokenValidation, logout } from "../actions/userActions";
import stockImg from "./img-not-found.jpg";
function HomePage() {
    const [cards, setCards] = useState([]);
  
    const [page, setPage] = useState(1);
  
     // products list reducer
  const productsListReducer = useSelector((state) => state.productsListReducer);
  const { loading, error, products } = productsListReducer;

  // login reducer
  const userLoginReducer = useSelector((state) => state.userLoginReducer);
  const { userInfo } = userLoginReducer;

  
  let history = useHistory();
  let searchTerm = history.location.search;
  const dispatch = useDispatch();


  useEffect(() => {
    dispatch(getProductsList());
    dispatch({
      type: CREATE_PRODUCT_RESET,
    });
    //dispatch(checkTokenValidation())
  }, [dispatch]);


    // function to fetch data from API
    const fetchData = async () => {
      setLoading(true);
      const response = await fetch(`https://jsonplaceholder.typicode.com/users?page=${page}&limit=10`);
      const data = await response.json();
      setCards([...cards, ...data]);
      setLoading(false);
    };
  
    useEffect(() => {
      fetchData();
      // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [page]);
  
    // function to handle infinite scrolling
    const handleScroll = () => {
      if (
        window.innerHeight + document.documentElement.scrollTop ===
        document.documentElement.offsetHeight
      ) {
        setPage(page + 1);
      }
    };
  
    useEffect(() => {
      window.addEventListener("scroll", handleScroll);
      return () => window.removeEventListener("scroll", handleScroll);
    }, []);
  
    return (
      <div className="card-container">
        {cards.map((card) => (
          <div key={card.id} className="card">
            <div className="card-header">
              <img src={`https://i.pravatar.cc/150?img=${card.id}`} alt={card.name} />
              <div className="card-header-info">
                <h3>{card.name}</h3>
                <p>{card.email}</p>
              </div>
            </div>
            <div className="card-body">
              <img src={`https://picsum.photos/400/300?random=${card.id}`} alt="" />
              <div className="card-body-info">
                <p>{card.company.catchPhrase}</p>
                <div className="card-icons">
                  <div className="icon">
                    <i className="far fa-heart"></i>
                    <span>12</span>
                  </div>
                  <div className="icon">
                    <i className="far fa-comment"></i>
                    <span>4</span>
                  </div>
                  <div className="icon">
                    <i className="fas fa-share"></i>
                  </div>
                  <div className="icon">
                    <i className="fas fa-shopping-cart"></i>
                  </div>
                </div>
              </div>
            </div>
          </div>
        ))}
        {loading && <div className="loading">Loading...</div>}
      </div>
    );
}

export default HomePage;
