import React, { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Form, Button } from "react-bootstrap";
import { createProduct } from "../actions/productActions";
import { useHistory } from "react-router";
import { checkTokenValidation, logout } from "../actions/userActions";
import { CREATE_PRODUCT_RESET } from "../constants";
import Message from "../components/Message";
import stockImg from "./img-not-found.jpg";

const ProductCreatePage = () => {
  let history = useHistory();
  const dispatch = useDispatch();
  const [product_from, setProduct_from] = useState("");
  const [name, setName] = useState("");
  const [description, setDescription] = useState("");
  const [price, setPrice] = useState("100");
  const [stock, setStock] = useState(false);
  const [image, setImage] = useState(null);
  const [image2, setImage2] = useState(stockImg);
  const [image3, setImage3] = useState(stockImg);

  // login reducer
  const userLoginReducer = useSelector((state) => state.userLoginReducer);
  const { userInfo } = userLoginReducer;

  // create product reducer
  const createProductReducer = useSelector(
    (state) => state.createProductReducer
  );
  const {
    product,
    success: productCreationSuccess,
    error: productCreationError,
  } = createProductReducer;

  // check token validation reducer
  const checkTokenValidationReducer = useSelector(
    (state) => state.checkTokenValidationReducer
  );
  const { error: tokenError } = checkTokenValidationReducer;

  useEffect(() => {
    if (!userInfo) {
      history.push("/login");
    }
    dispatch(checkTokenValidation());
  }, [dispatch, userInfo, history]);

  const onSubmit = (e) => {
    e.preventDefault();

    let form_data = new FormData();
    form_data.append("product_from",product_from);
    form_data.append("name", name);
    form_data.append("description", description);
    form_data.append("price", price);
    form_data.append("stock", stock);
    form_data.append("image", image);
    form_data.append("image2", image2);
    form_data.append("image3", image3);

    dispatch(createProduct(form_data));
  };

  if (productCreationSuccess) {
    alert("Product successfully created.");
    history.push(`/product/${product.id}/`);
    dispatch({
      type: CREATE_PRODUCT_RESET,
    });
  }

  if (userInfo && tokenError === "Request failed with status code 401") {
    alert("Session expired, please login again.");
    dispatch(logout());
    history.push("/login");
    window.location.reload();
  }

  return (
    <div>
      {productCreationError && (
        <Message variant="danger">{productCreationError.image[0]}</Message>
      )}
      <span className="d-flex justify-content-center text-info">
        <em>New Product</em>
      </span>
      <Form onSubmit={onSubmit}>
        <Form.Group controlId="name">
          <Form.Label>
            <b>Product Name</b>
          </Form.Label>
          <Form.Control
            required
            autoFocus={true}
            type="text"
            value={name}
            placeholder="product name"
            onChange={(e) => {
              setName(e.target.value)
              setProduct_from(userInfo.username)
            }}
          ></Form.Control>
        </Form.Group>

        <Form.Group controlId="description">
          <Form.Label>
            <b>Product Description</b>
          </Form.Label>
          <Form.Control
            required
            type="text"
            value={description}
            placeholder="product description"
            onChange={(e) => setDescription(e.target.value)}
          ></Form.Control>
        </Form.Group>

        <Form.Group controlId="price">
          <Form.Label>
            <b>Price</b>
          </Form.Label>
          <Form.Control
            required
            type="text"
            pattern="[0-9]+(\.[0-9]{1,2})?%?"
            value={price}
            placeholder="199.99"
            step="0.01"
            maxLength="8"
            onChange={(e) => setPrice(e.target.value)}
          ></Form.Control>
        </Form.Group>

        <span style={{ display: "flex" }}>
          <label>In Stock</label>
          <input
            type="checkbox"
            value={stock}
            className="ml-2 mt-2"
            onChange={() => setStock(!stock)}
          />
        </span>

        <Form.Group controlId="image">
          <Form.Label>
            <b>Product Image</b>
          </Form.Label>
          <Form.Control
            required
            type="file"
            onChange={(e) =>{ setImage(e.target.files[0])}}
          ></Form.Control>

        </Form.Group>
        <Form.Group controlId="image2">
          <Form.Label>
            <b>Product Image</b>
          </Form.Label>
          <Form.Control 
            type="file"
            onChange={(e) => setImage2(e.target.files[0])}
          ></Form.Control>

        </Form.Group>
        <Form.Group controlId="image3">
          <Form.Label>
            <b>Product Image</b>
          </Form.Label>
          <Form.Control
            type="file"
            onChange={(e) => setImage3(e.target.files[0])}
          ></Form.Control>
        </Form.Group>
        <Button
          type="submit"
          variant="success"
          className="btn-sm button-focus-css"
        >
          Save Product
        </Button>
        <Button
          type="submit"
          variant="primary"
          className="btn-sm ml-2 button-focus-css"
          onClick={() => {
            history.push("/")
          }
        }
        >
          Cancel
        </Button>
      </Form>
    </div>
  );
};

export default ProductCreatePage;
